mapper.select:
select articleNo, user_id, subject, content, register_time, heart_count, imageUrl, imageName, sido_code
from hotplace
where articleNo = #{articleNo}

mapper.update:
update hotplace
set subject = #{subject}, content=#{content}, imageUrl=#{imageUrl}, imageName=#{imageName}, sido_code=#{sido_code}
where articleNo = #{articleNo}
