mapper.select:
	select a.articleNo,b.articleNo,a.content_id, a.start_date, a.end_date, a.subject,a.user_id, b.travelplan_id,b.title,b.addr1 , b.first_image,a.register_time,a.star_count
from travelplan a, travelplaninfo b
where b.articleNo= #{articleNo}
and a.articleNo = #{articleNo}

mapper.update:
update travelplan
set start_date = #{startDate}, end_date = #{endDate},subject = #{subject},star_count= #{starCount}
where articleNo = #{articleNo}

		update travelplaninfo
   	 set content_id=#{contentId},title=#{title}, addr1= #{address}, first_image=#{image}
   	 where articleNo = #{articleNo}
   	 and travelplan_id= #{travelplanId}
