mapper.select:
select count(user_id)
from user
where user_id = #{id}

select user_id, user_name
from user
where user_id = #{userId} and user_pw = #{userPwd}

select user_id, user_name, user_pw, email_id, email_domain, join_date, admin, profile_image
from user
where user_id = #{userId}

mapper.update:
update user
user_pw = #{userPwd}, email_id = #{emailId}, email_domain = #{emailDomain},
where user_id = #{userId}
